import React, { useState } from 'react';
import { startSpeechRecognition, stopRecognition } from './services/audioRecording';
import NotesUI from './components/NotesUI';
import axios from 'axios';

function App() {
    const [isRecording, setIsRecording] = useState(false);
    const [finalTranscript, setFinalTranscript] = useState(''); // State for final transcript
    const [generatedNotes, setGeneratedNotes] = useState({});

    const handleStartRecording = async () => {
        if (isRecording) return;
        setIsRecording(true);
        try {
            // Start the speech recognition and listen for updates
            await startSpeechRecognition((transcript) => {
                setFinalTranscript(transcript); // Update final transcript
            });
        } catch (error) {
            console.error("Error starting speech recognition:", error);
            setIsRecording(false);
        }
    };

    const handleStopRecording = async () => {
        if (!isRecording) return;
        try {
            // Stop the speech recognition
            await stopRecognition();

            // Send the final transcript to the backend for note generation
            const response = await axios.post('http://localhost:3001/transcribe', { transcript: finalTranscript });
            setGeneratedNotes(response.data.notes);

        } catch (error) {
            console.error("Error stopping speech recognition or sending transcript:", error);
        } finally {
            setIsRecording(false);
        }
    };

    return (
        <div className="App">
            <h1>Speech to Notes</h1>
            <button onClick={handleStartRecording} disabled={isRecording}>
                Start Recording
            </button>
            <button onClick={handleStopRecording} disabled={!isRecording}>
                Stop Recording
            </button>
            <NotesUI notes={generatedNotes} />
        </div>
    );
}

export default App;
