/* COMMENTED OUT MEDIARECORDER-BASED HANDLERS */
import React, { useState } from 'react';
import { startRecording, stopRecording } from './services/audioRecording';
import { sendTranscriptToBackend } from './services/noteService'; // Adjust path as needed

function App() {
    const [isRecording, setIsRecording] = useState(false); // State to control recording
    const [generatedNotes, setGeneratedNotes] = useState(''); // State to store generated notes

    const handleStartRecording = async () => {
        if (isRecording) return;
        setIsRecording(true);
        try {
            await startRecording();
        } catch (error) {
            console.error("Error starting recording:", error);
            setIsRecording(false);
        }
    };

    const handleStopRecording = async () => {
        if (!isRecording) return;
        try {
            await stopRecording();
            const transcript = await sendTranscriptToBackend(); // Get the transcript after recording
            setGeneratedNotes(transcript); // Set the generated notes in the state
        } catch (error) {
            console.error("Error stopping recording:", error);
        } finally {
            setIsRecording(false);
        }
    };

    return (
        <div className="App">
            <h1>Zoom/Google Meet Audio Recorder</h1>
            <button onClick={handleStartRecording} disabled={isRecording}>
                Start Recording
            </button>
            <button onClick={handleStopRecording} disabled={!isRecording}>
                Stop Recording
            </button>

            {/* Display the generated notes */}
            {generatedNotes && (
                <div>
                    <h2>Generated Notes</h2>
                    <pre>{generatedNotes}</pre>
                </div>
            )}
        </div>
    );
}

export default App;
