import React, { useState } from 'react';
import { startSpeechRecognition, stopRecognition } from './services/audioRecording';
import NotesUI from './components/NotesUI';  // Assuming this is your notes UI component

function App() {
    const [isRecording, setIsRecording] = useState(false);
    const [generatedNotes, setGeneratedNotes] = useState({});

    const handleStartRecording = async () => {
        setIsRecording(true);
        try {
            await startSpeechRecognition();
        } catch (error) {
            console.error("Error starting speech recognition:", error);
            setIsRecording(false);
        }
    };

    const handleStopRecording = async () => {
        setIsRecording(false);
        try {
            await stopRecognition();
            // Sending final transcript to the server
            if (finalTranscript.trim()) {
                console.log("Sending final transcript to server:", finalTranscript);
                const response = await axios.post('http://localhost:3001/transcribe', {
                    transcript: finalTranscript,
                });
                setGeneratedNotes(response.data.notes);
            } else {
                console.log("Final transcript is empty. No data to send.");
            }
        } catch (error) {
            console.error("Error stopping speech recognition or sending transcript:", error);
        }
    };

    return (
        <div className="App">
            <h1>Speech to Notes</h1>
            <button onClick={handleStartRecording} disabled={isRecording}>
                Start Recording
            </button>
            <button onClick={handleStopRecording} disabled={!isRecording}>
                Stop Recording
            </button>

            <NotesUI notes={generatedNotes} />

            {/* Add any wallpaper or background styling here */}
            <div className="wallpaper">Your Wallpaper Content Here</div>
        </div>
    );
}

export default App;
