import React, { useState } from 'react';
import axios from 'axios';
import NotesUI from './components/NotesUI';
import { startSpeechRecognition, stopRecognition, finalTranscript } from './services/audioRecording';

function App() {
    const [isRecording, setIsRecording] = useState(false);
    const [generatedNotes, setGeneratedNotes] = useState({});

    const handleStartRecording = async () => {
        if (isRecording) return;
        setIsRecording(true);
        try {
            await startSpeechRecognition();
        } catch (error) {
            console.error("Error starting speech recognition:", error);
            setIsRecording(false);
        }
    };

    const handleStopRecording = async () => {
        try {
            console.log("Stopping recognition...");
            stopRecognition();

            // Introduce a small delay to allow the final transcript to be fully captured
            await new Promise(resolve => setTimeout(resolve, 600)); 

            // Log the final transcript for debugging
            console.log("Final Transcript after recognition:", finalTranscript);

            // Get final transcript from speech recognition
            if (!finalTranscript || finalTranscript.trim() === '') {
                console.log("Final transcript is empty.");
                return;
            }

            console.log("Sending final transcript to server:", finalTranscript);

            // Send the transcript to the backend for note generation
            const response = await axios.post('http://localhost:3001/transcribe', { transcript: finalTranscript });
            const notes = response.data.notes;

            console.log("Received notes:", notes);
            setGeneratedNotes(notes);  // Pass the notes to your NotesUI component
        } catch (error) {
            console.error("Error stopping speech recognition or sending transcript:", error);
        }
    };

    return (
        <div className="App">
            <h1>Speech to Notes</h1>
            <button onClick={handleStartRecording} disabled={isRecording}>
                Start Recording
            </button>
            <button onClick={handleStopRecording} disabled={!isRecording}>
                Stop Recording
            </button>
            <NotesUI notes={generatedNotes} /> {/* Pass generated notes to your NotesUI */}
        </div>
    );
}

export default App;
