let isRecognitionRunning = false;
let isRestarting = false;
let recognition;
let isRecognitionInitialized = false;
let userStartedRecognition = false; // New flag to track if the user explicitly started the recognition
const COOLDOWN_DURATION = 3000; // Cooldown period before restarting
const RESTART_DELAY = 1000; // Delay between stop and start

// Log messages
function logWithLimit(message) {
  const timestamp = new Date().toISOString();
  console.log(`[${timestamp}] ${message}`);
}

// Request microphone permission
async function requestMicPermission() {
  try {
    const stream = await navigator.mediaDevices.getUserMedia({
      audio: true
    });
    stream.getTracks().forEach(track => track.stop()); // Stop stream to release the mic
    return true;
  } catch (error) {
    alert('Microphone access is required for speech recognition.');
    return false;
  }
}

// Initialize SpeechRecognition
function initializeRecognition() {
  if (!window.SpeechRecognition && !window.webkitSpeechRecognition) {
    alert('Your browser does not support the SpeechRecognition API.');
    return;
  }
  window.SpeechRecognition = window.SpeechRecognition || window.webkitSpeechRecognition;
  recognition = new window.SpeechRecognition();
  recognition.continuous = true;
  recognition.interimResults = true;
  recognition.lang = 'en-US';
  recognition.onresult = event => {
    let interimTranscript = '';
    let finalTranscript = '';

    // Iterate over recognition results
    for (let i = event.resultIndex; i < event.results.length; i++) {
      const result = event.results[i];
      if (result.isFinal) {
        finalTranscript += result[0].transcript + ' ';
        logWithLimit('Final Transcript So Far: ' + finalTranscript);
      } else {
        interimTranscript += result[0].transcript;
      }
    }

    // Add delay if necessary, then append interim transcript
    setTimeout(() => {
      logWithLimit('Interim Transcript: ' + interimTranscript);
    }, 200);
  };
  recognition.onerror = event => {
    logWithLimit('Recognition error: ' + event.error);
    if (event.error === 'aborted' || event.error === 'no-speech') {
      logWithLimit('Recognition aborted, waiting before restart...');
      handleRestartWithCooldown();
    }
  };
  recognition.onend = () => {
    if (userStartedRecognition && isRecognitionRunning && !isRestarting) {
      logWithLimit('Recognition ended, but user requested it to keep running.');
      handleRestartWithCooldown();
    } else {
      logWithLimit('Recognition ended normally.');
    }
  };
  isRecognitionInitialized = true;
  logWithLimit('Recognition initialized.');
}

// Start recognition only when manually triggered
function startRecognition() {
  if (!isRecognitionInitialized) {
    logWithLimit('Cannot start recognition; initialization is not complete.');
    return;
  }
  if (!isRecognitionRunning && !isRestarting && userStartedRecognition) {
    logWithLimit('Starting recognition...');
    isRecognitionRunning = true;
    try {
      recognition.start();
    } catch (error) {
      logWithLimit('Error starting recognition: ' + error);
    }
  } else {
    logWithLimit('Recognition is already running or restarting.');
  }
}

// Cooldown before restarting recognition
function handleRestartWithCooldown() {
  if (!userStartedRecognition || !isRecognitionRunning || isRestarting) {
    return;
  }
  isRestarting = true;
  logWithLimit('Cooldown initiated, will restart in a few seconds...');
  setTimeout(() => {
    if (isRecognitionRunning && userStartedRecognition) {
      logWithLimit('Restarting recognition...');
      stopAndRestartRecognition(); // Stop and restart with delay
    }
    isRestarting = false;
  }, COOLDOWN_DURATION); // Wait before restarting
}

// Stop and restart recognition with delay
function stopAndRestartRecognition() {
  try {
    recognition.stop(); // Stop first
    setTimeout(() => {
      if (isRecognitionRunning && userStartedRecognition && !isRestarting) {
        try {
          logWithLimit('Attempting to restart recognition...');
          recognition.start(); // Then restart after delay
        } catch (error) {
          logWithLimit('Error restarting recognition: ' + error);
        }
      }
    }, RESTART_DELAY); // Wait before starting again
  } catch (error) {
    logWithLimit('Error stopping recognition: ' + error);
  }
}

// Stop recognition
export function stopRecognition() {
  if (isRecognitionRunning) {
    logWithLimit('Stopping recognition...');
    try {
      recognition.stop();
    } catch (error) {
      logWithLimit('Error stopping recognition: ' + error);
    }
    isRecognitionRunning = false;
    isRestarting = false;
    userStartedRecognition = false; // Mark recognition as manually stopped by the user
  } else {
    logWithLimit('Recognition is not running.');
  }
}

// Start speech recognition process when manually triggered
export async function startSpeechRecognition() {
  const permissionGranted = await requestMicPermission();
  if (!permissionGranted) return;
  if (!isRecognitionInitialized) {
    initializeRecognition();
  }
  userStartedRecognition = true; // Track that the user started the recognition
  startRecognition();
}

