/* COMMENTED OUT MEDIARECORDER-BASED HANDLERS */
import React, { useState } from 'react';
import NotesUI from './components/NotesUI';
import { startRecording, stopRecording, generateNotesFromTranscript } from './services/audioRecording';
import { sendTranscriptToBackend } from './services/noteService';

function App() {
    const [isRecording, setIsRecording] = useState(false);
    const [generatedNotes, setGeneratedNotes] = useState(null);  // State for generated notes

    const handleStartRecording = async () => {
        setIsRecording(true);
        await startRecording();
    };

    const handleStopRecording = async () => {
        await stopRecording();
        const notes = await generateNotesFromTranscript();  // Get the notes from the function
        if (notes) {
            setGeneratedNotes(notes);  // Update state with the notes
        }
        setIsRecording(false);
    };

    return (
        <div className="App">
            <h1>Audio Notes</h1>
            <button onClick={handleStartRecording} disabled={isRecording}>
                Start Recording
            </button>
            <button onClick={handleStopRecording} disabled={!isRecording}>
                Stop Recording
            </button>
            {generatedNotes && (
                <div>
                    <h2>Generated Notes:</h2>
                    <pre>{generatedNotes}</pre>
                </div>
            )}
        </div>
    );
}

export default App;
