/* COMMENTED OUT MEDIARECORDER-BASED HANDLERS */
import React, { useState } from 'react';
import NotesUI from './components/NotesUI';
import { startRecording, stopRecording, generateNotesFromTranscript } from './services/audioRecording';
import { sendTranscriptToBackend } from './services/noteService';

function App() {
    const [isRecording, setIsRecording] = useState(false);
    const [generatedNotes, setGeneratedNotes] = useState(null); // State to store the notes

    const handleStartRecording = async () => {
        if (isRecording) return;
        setIsRecording(true);
        try {
            await startRecording();
        } catch (error) {
            console.error("Error starting recording:", error);
            setIsRecording(false);
        }
    };

    const handleStopRecording = async () => {
        if (!isRecording) return;
        try {
            await stopRecording();
            await generateNotesFromTranscript(setGeneratedNotes); // Pass the setGeneratedNotes function
        } catch (error) {
            console.error("Error stopping recording:", error);
        } finally {
            setIsRecording(false);
        }
    };

    return (
        <div className="App">
            <h1>Zoom/Google Meet Audio Recorder</h1>
            <button onClick={handleStartRecording} disabled={isRecording}>
                Start Recording
            </button>
            <button onClick={handleStopRecording} disabled={!isRecording}>
                Stop Recording
            </button>

            {/* Render NotesUI with generated notes */}
            {generatedNotes && <NotesUI notes={generatedNotes} />}
        </div>
    );
}

export default App;
