function checkSpeechRecognitionSupport() {
  window.SpeechRecognition = window.SpeechRecognition || window.webkitSpeechRecognition;
  if (!window.SpeechRecognition) {
    console.error('SpeechRecognition API not supported.');
    alert('Your browser does not support speech recognition features.');
    return false;
  }
  return true;
}

// Check for WebkitSpeechRecognition support early
if (!checkSpeechRecognitionSupport()) {
  alert('Your browser does not support the necessary speech recognition features.');
}

// Initialize WebkitSpeechRecognition
function initializeRecognition() {
  recognition = new window.SpeechRecognition();
  recognition.continuous = true; // Allow continuous recognition
  recognition.interimResults = true; // Capture partial results for real-time updates
  recognition.lang = 'en-US'; // Set recognition language

  // Handle recognition results (interim and final)
  recognition.onresult = event => {
    let interimTranscript = '';

    // Iterate through results starting from the event.resultIndex
    for (let i = event.resultIndex; i < event.results.length; i++) {
      const result = event.results[i];

      // If the result is final, append it to the final transcript
      if (result.isFinal) {
        finalTranscript += result[0].transcript; // Append to final transcript
        console.log('Final Transcript So Far:', finalTranscript); // Log final transcript
      } else {
        interimTranscript += result[0].transcript; // Append interim transcripts
      }
    }

    // Check if the interim transcript is new and has changed significantly
    if (interimTranscript !== lastLoggedInterimTranscript && interimTranscript.length >= INTERIM_THRESHOLD) {
      finalTranscript += interimTranscript; // Append interim transcript to final transcript
      lastLoggedInterimTranscript = interimTranscript; // Update last logged interim
      console.log('Appended Interim Transcript to Final:', finalTranscript);
    }

    // Log interim transcript only when it's new or updated
    if (interimTranscript && interimTranscript !== lastLoggedInterimTranscript) {
      console.log('Updated Interim Transcript:', interimTranscript);
    }
  };

  // Handle when recognition ends and restart if necessary (without sending transcript)
  recognition.onend = () => {
    console.log('Speech recognition ended.');

    // Only restart recognition if it wasn't stopped manually
    if (isRecognitionRunning) {
      console.log('Restarting speech recognition...');
      recognition.start(); // Automatically restart recognition
    }
  };

  // Handle errors during recognition
  recognition.onerror = event => {
    console.error('Speech Recognition Error:', event.error);
    if (event.error === 'no-speech') {
      alert('No speech detected. Please try again.');
      setTimeout(() => {
        startSpeechRecognition();
      }, 1000); // Restart after 1 second if no speech is detected
    }
    if (event.error === 'audio-capture') {
      alert('Please check your microphone permissions.');
    }

    // Handle other potential errors
    if (event.error === 'aborted' || event.error === 'network') {
      console.error('Speech recognition was aborted or there was a network issue.');
      recognition.start(); // Restart if it's a recoverable error
    }
  };
}

