/* COMMENTED OUT MEDIARECORDER-BASED HANDLERS */
import React, { useState } from 'react';
import { startRecording, stopRecording, sendTranscriptToBackend } from './services/audioRecording'; // Adjust path as needed

function App() {
    const [isRecording, setIsRecording] = useState(false);  // To control recording state
    const [notes, setNotes] = useState('');  // To store generated notes

    const handleStartRecording = async () => {
        if (isRecording) return;
        setIsRecording(true);
        try {
            await startRecording();
        } catch (error) {
            console.error("Error starting recording:", error);
            setIsRecording(false);
        }
    };

    const handleStopRecording = async () => {
        if (!isRecording) return;
        try {
            await stopRecording();

            // After stopping the recording, get the notes
            const generatedNotes = await sendTranscriptToBackend(); // Assuming this sends the transcript to backend
            if (generatedNotes) {
                setNotes(generatedNotes);  // Store the notes in state
            }
        } catch (error) {
            console.error("Error stopping recording:", error);
        } finally {
            setIsRecording(false);
        }
    };

    return (
        <div className="App">
            <h1>Zoom/Google Meet Audio Recorder</h1>
            <button onClick={handleStartRecording} disabled={isRecording}>
                Start Recording
            </button>
            <button onClick={handleStopRecording} disabled={!isRecording}>
                Stop Recording
            </button>

            {/* Display the generated notes */}
            {notes && (
                <div>
                    <h2>Generated Notes</h2>
                    <pre>{notes}</pre> {/* Render the notes in a <pre> tag to preserve formatting */}
                </div>
            )}
        </div>
    );
}

export default App;
