let isRecognitionRunning = false;
let isRestarting = false;
let finalTranscript = ''; 
let recognition;
let isRecognitionInitialized = false;
const INTERIM_THRESHOLD = 40;  // Threshold for interim transcript length before appending
let lastLoggedInterimTranscript = '';  
let lastRecognitionTime = Date.now();  // Time of the last recognition result
const TIMEOUT_DURATION = 7000;  // Increase timeout duration to handle buffer/stutter

let restartTimeout = null;  // Add a variable to track timeout for restart attempts

// Function to log messages
function logWithLimit(message) {
    const timestamp = new Date().toISOString();
    console.log(`[${timestamp}] ${message}`);
}

// Function to request microphone permission
async function requestMicPermission() {
    try {
        const stream = await navigator.mediaDevices.getUserMedia({ audio: true });
        stream.getTracks().forEach(track => track.stop());  // Stop the stream after permission is granted
        return true;
    } catch (error) {
        alert('Microphone access is required for speech recognition.');
        return false;
    }
}

// Initialize SpeechRecognition
function initializeRecognition() {
    window.SpeechRecognition = window.SpeechRecognition || window.webkitSpeechRecognition;

    if (!window.SpeechRecognition) {
        alert('Your browser does not support the SpeechRecognition API.');
        return;
    }

    recognition = new window.SpeechRecognition();
    recognition.continuous = true;  
    recognition.interimResults = true;  
    recognition.lang = 'en-US';  

    recognition.onresult = (event) => {
        let interimTranscript = '';
        lastRecognitionTime = Date.now();

        for (let i = event.resultIndex; i < event.results.length; i++) {
            const result = event.results[i];
            if (result.isFinal) {
                finalTranscript += result[0].transcript.trim() + ' ';
                logWithLimit('Final Transcript So Far: ' + finalTranscript);
            } else {
                if (result[0].transcript.length > interimTranscript.length) {
                    interimTranscript = result[0].transcript;
                }
            }
        }

        if (interimTranscript.length > INTERIM_THRESHOLD && interimTranscript !== lastLoggedInterimTranscript) {
            if (interimTranscript.length > lastLoggedInterimTranscript.length) {
                lastLoggedInterimTranscript = interimTranscript;
                logWithLimit('Interim Transcript: ' + interimTranscript);
            }
        }
    };

    recognition.onerror = (event) => {
        logWithLimit('Recognition error: ' + event.error);
        if (event.error === 'aborted') {
            logWithLimit('Recognition aborted, not restarting immediately.');
        } else {
            restartRecognition();
        }
    };

    recognition.onend = () => {
        logWithLimit('Recognition ended.');
        if (isRecognitionRunning && !isRestarting) {
            restartRecognition();
        }
    };

    isRecognitionInitialized = true;
    logWithLimit('Recognition initialized.');
}

// Start SpeechRecognition if initialized
function startRecognition() {
    if (!isRecognitionInitialized) {
        logWithLimit('Cannot start recognition; initialization is not complete.');
        return;
    }

    if (!isRecognitionRunning) {
        logWithLimit('Starting recognition...');
        isRecognitionRunning = true;
        recognition.start();
    }
}

// Restart recognition with a debounce
function restartRecognition() {
    if (isRestarting || !isRecognitionRunning) {
        logWithLimit('Already restarting or not running, skipping restart.');
        return;
    }

    logWithLimit('Stopping recognition before restarting...');
    recognition.stop();

    isRestarting = true;

    clearTimeout(restartTimeout);  // Clear previous restart timeout to prevent stacking
    restartTimeout = setTimeout(() => {
        if (isRecognitionRunning && isRecognitionInitialized) {
            logWithLimit('Restarting recognition...');
            recognition.start();
            isRestarting = false;
        }
    }, 2000);  // Wait 2 seconds before attempting to restart to prevent loops
}

// Stop recognition and clear timeouts
export function stopRecognition() {
    if (isRecognitionRunning) {
        logWithLimit('Stopping recognition...');
        clearTimeout(restartTimeout);  // Stop any pending restart
        recognition.stop();
        isRecognitionRunning = false;
        isRestarting = false;
    }
}

// Start SpeechRecognition process manually
export async function startSpeechRecognition() {
    const permissionGranted = await requestMicPermission();  
    if (!permissionGranted) return;

    if (!isRecognitionInitialized) {
        initializeRecognition();
    }

    startRecognition();
}
