import React, { useState } from 'react';
import { startSpeechRecognition, stopRecognition } from './services/audioRecording';
import NotesUI from './components/NotesUI';
import axios from 'axios';

function App() {
    const [isRecording, setIsRecording] = useState(false);
    const [finalTranscript, setFinalTranscript] = useState(''); // State for final transcript
    const [generatedNotes, setGeneratedNotes] = useState({});

    // Callback to update the final transcript
    const updateFinalTranscript = (transcript) => {
        setFinalTranscript(transcript);
    };

    const handleStartRecording = async () => {
        if (isRecording) return;
        setIsRecording(true);
        try {
            // Pass the callback to startSpeechRecognition
            await startSpeechRecognition(updateFinalTranscript);
        } catch (error) {
            console.error("Error starting speech recognition:", error);
            setIsRecording(false);
        }
    };

    const handleStopRecording = async () => {
        if (!isRecording) return; 
        try {
            // Stopping recognition
            await stopRecognition();

            // Check if finalTranscript has data
            if (!finalTranscript || finalTranscript.trim() === '') {
                console.error("Final transcript is empty. No data to send.");
                return;  // Early return if finalTranscript is empty
            }

            console.log('Final transcript being sent:', finalTranscript); // Log to ensure finalTranscript has data

            // Send transcript to backend
            const response = await axios.post('http://localhost:3001/transcribe', {
                transcript: finalTranscript.trim()  // Send final transcript as JSON, ensure it's not empty
            });

            console.log('Transcription response:', response.data);
            setGeneratedNotes(response.data.notes); // Update generated notes state
        } catch (error) {
            console.error("Error stopping speech recognition or sending transcript:", error);
        } finally {
            setIsRecording(false);
        }
    };


    return (
        <div className="App">
            <h1>Speech to Notes</h1>
            <button onClick={handleStartRecording} disabled={isRecording}>
                Start Recording
            </button>
            <button onClick={handleStopRecording} disabled={!isRecording}>
                Stop Recording
            </button>
            <NotesUI notes={generatedNotes} />
        </div>
    );
}

export default App;
