import React, { useState } from 'react';
import { startSpeechRecognition, stopRecognition } from './services/audioRecording'; // Adjust path as needed
import NotesUI from './components/NotesUI';
import axios from 'axios';

function App() {
    const [isRecording, setIsRecording] = useState(false);
    const [generatedNotes, setGeneratedNotes] = useState({});

    const handleStartRecording = async () => {
        if (isRecording) return;
        setIsRecording(true);
        try {
            await startSpeechRecognition();
        } catch (error) {
            console.error("Error starting speech recognition:", error);
            setIsRecording(false);
        }
    };

    const handleStopRecording = async () => {
        if (!isRecording) return;
        try {
            stopRecognition(async (transcript) => {
                if (transcript) {
                    console.log("Sending final transcript to server:", transcript);

                    // Send the final transcript to the backend
                    const response = await axios.post('http://localhost:3001/transcribe', { transcript });
                    console.log("Received notes:", response.data.notes);
                    setGeneratedNotes(response.data.notes);
                } else {
                    console.log("Final transcript is empty. No data to send.");
                }
            });
        } catch (error) {
            console.error("Error stopping speech recognition or sending transcript:", error);
        } finally {
            setIsRecording(false);
        }
    };


    return (
        <div className="App">
            <h1>Speech to Notes</h1>
            <button onClick={handleStartRecording} disabled={isRecording}>
                Start Recording
            </button>
            <button onClick={handleStopRecording} disabled={!isRecording}>
                Stop Recording
            </button>
            <NotesUI notes={generatedNotes} />
        </div>
    );
}

export default App;
