import React, { useState } from 'react';
import axios from 'axios';
import NotesUI from './components/NotesUI';
import { startSpeechRecognition, stopRecognition, getFinalTranscript } from './services/audioRecording';

function App() {
    const [isRecording, setIsRecording] = useState(false);
    const [generatedNotes, setGeneratedNotes] = useState({});

    const handleStartRecording = async () => {
        if (isRecording) return;  // Prevent starting if already recording
        setIsRecording(true);  // Enable "Stop Recording" and disable "Start Recording"

        try {
            await startSpeechRecognition();
        } catch (error) {
            console.error("Error starting speech recognition:", error);
            setIsRecording(false);  // Re-enable "Start Recording" if error occurs
        }
    };

    const handleStopRecording = async () => {
        try {
            console.log("Stopping recognition...");
            stopRecognition();  // Stops the recognition process

            // Small delay to ensure final transcript is captured
            await new Promise(resolve => setTimeout(resolve, 500)); 

            const finalTranscript = getFinalTranscript();
            console.log("Final Transcript after recognition:", finalTranscript);

            // Check if final transcript is empty and skip sending to server if it is
            if (!finalTranscript || finalTranscript.trim() === '') {
                console.log("Final transcript is empty. Not sending to server.");
                return;
            }

            console.log("Sending final transcript to server...");
            const response = await axios.post('http://localhost:3001/transcribe', { transcript: finalTranscript });
            const notes = response.data.notes;
            console.log("Received notes:", notes);
            setGeneratedNotes(notes);  // Pass notes to your NotesUI component

        } catch (error) {
            console.error("Error stopping speech recognition or sending transcript:", error);
        }
    };


    return (
        <div className="App">
            <h1>Speech to Notes</h1>
            <button onClick={handleStartRecording} disabled={isRecording}> {/* Disable if already recording */}
                Start Recording
            </button>
            <button onClick={handleStopRecording} disabled={!isRecording}> {/* Disable if not recording */}
                Stop Recording
            </button>
            <NotesUI notes={generatedNotes} /> {/* Pass generated notes to NotesUI */}
        </div>
    );
}

export default App;
