function checkSpeechRecognitionSupport() {
  window.SpeechRecognition = window.SpeechRecognition || window.webkitSpeechRecognition;
  if (!window.SpeechRecognition) {
    console.error('SpeechRecognition API not supported.');
    alert('Your browser does not support speech recognition features.');
    return false;
  }
  return true;
}

// Check for WebkitSpeechRecognition support early
if (!checkSpeechRecognitionSupport()) {
  alert('Your browser does not support the necessary speech recognition features.');
}

// Initialize WebkitSpeechRecognition
let recognition;
let isRecognitionRunning = false;
let finalTranscript = ''; // Store final transcript globally

function initializeRecognition() {
  recognition = new window.SpeechRecognition();
  recognition.continuous = true;
  recognition.interimResults = true; // Set to true to capture partial results
  recognition.lang = 'en-US';
  recognition.onresult = event => {
    let interimTranscript = '';

    // Iterate through the results starting from the event.resultIndex
    for (let i = event.resultIndex; i < event.results.length; i++) {
      const result = event.results[i];

      // If the result is final, append it to the finalTranscript
      if (result.isFinal) {
        finalTranscript += result[0].transcript; // Append the final transcript
      } else {
        interimTranscript += result[0].transcript; // Append interim transcripts
      }
    }

    // Log interim transcript (optional: remove this if you don't want to see live updates)
    console.log('Interim Transcript:', interimTranscript);

    // Log the final transcript only once after the recognition session ends
    if (event.results[event.results.length - 1].isFinal) {
      console.log('Final Transcript:', finalTranscript); // Only log when the transcript is finalized
    }
  };

  // Handle when recognition ends, and restart if necessary
  recognition.onend = () => {
    console.log('Speech recognition stopped.');
    isRecognitionRunning = false;

    // Restart if still needed
    if (isRecognitionRunning) {
      console.log('Restarting speech recognition...');
      recognition.start(); // Restart recognition automatically
    }
  };

  // Handle errors in recognition
  recognition.onerror = event => {
    console.error('Speech Recognition Error:', event.error);
    if (event.error === 'no-speech') {
      alert('No speech detected. Please try again.');
      setTimeout(() => {
        startSpeechRecognition();
      }, 1000); // Restart after 1 second
    }
    if (event.error === 'audio-capture') {
      alert('Please check your microphone permissions.');
    }
  };
}

