import React, { useState } from 'react';
import { startSpeechRecognition, stopRecognition } from './services/audioRecording';
import NotesUI from './components/NotesUI';
import axios from 'axios';
import { organizeNotesByTopic } from './b/openAIService'; // Ensure this import is correct

function App() {
    const [isRecording, setIsRecording] = useState(false);
    const [generatedNotes, setGeneratedNotes] = useState({});

    const handleStartRecording = async () => {
        if (isRecording) return;
        setIsRecording(true);
        try {
            await startSpeechRecognition();
        } catch (error) {
            console.error("Error starting speech recognition:", error);
            setIsRecording(false);
        }
    };

    const handleStopRecording = async () => {
        try {
            console.log("Stopping recognition...");
            stopRecognition();

            // Small delay to ensure final transcript is captured
            await new Promise(resolve => setTimeout(resolve, 500)); 

            const finalTranscript = window.finalTranscript; // Assuming this is set somewhere in the speech recognition logic
            if (!finalTranscript || finalTranscript.trim() === '') {
                console.log("Final transcript is empty.");
                return;
            }

            console.log("Sending final transcript to server:", finalTranscript);
            const response = await axios.post('http://localhost:3001/transcribe', { transcript: finalTranscript });
            const rawNotes = response.data.notes;

            // Call `organizeNotesByTopic` to group the notes
            const organizedNotes = organizeNotesByTopic(rawNotes);
            console.log("Organized Notes:", organizedNotes);
            setGeneratedNotes(organizedNotes); // Pass the organized notes to NotesUI

        } catch (error) {
            console.error("Error stopping speech recognition or sending transcript:", error);
        }
    };

    return (
        <div className="App">
            <h1>Speech to Notes</h1>
            <button onClick={handleStartRecording} disabled={isRecording}>
                Start Recording
            </button>
            <button onClick={handleStopRecording} disabled={!isRecording}>
                Stop Recording
            </button>
            <NotesUI notes={generatedNotes} />
        </div>
    );
}

export default App;
