/* COMMENTED OUT MEDIARECORDER-BASED HANDLERS */
import React, { useState } from 'react';
import NotesUI from './components/NotesUI';
import { startRecording, stopRecording, generateNotesFromTranscript } from './services/audioRecording';


function App() {
    const [isRecording, setIsRecording] = useState(false);  // Track whether recording is active
    const [generatedNotes, setGeneratedNotes] = useState({});  // Track notes

    const handleStartRecording = async () => {
        if (!isRecording) {
            setIsRecording(true);
            await startRecording();
        }
    };

    const handleStopRecording = async () => {
        if (isRecording) {
            setIsRecording(false);
            await stopRecording(setGeneratedNotes);
        }
    };

    return (
        <div>
            <h1>Audio Notes</h1>
            <button onClick={handleStartRecording} disabled={isRecording}>Start Recording</button>
            <button onClick={handleStopRecording} disabled={!isRecording}>Stop Recording</button>
            <NotesUI notes={generatedNotes} />
        </div>
    );
}

export default App;
