import React from 'react';
import './NotesUI.css';
import { jsPDF } from "jspdf";
import html2canvas from 'html2canvas';

const NotesUI = ({ notes = {} }) => {
    console.log('Notes received:', notes);  // Log received notes

    const renderNotes = () => {
        if (Object.keys(notes).length === 0) {
            return <div>No notes available</div>;
        }

        return Object.entries(notes).map(([topic, notesList]) => (
            <div key={topic}>
                <div className="topic">{topic}</div>
                {Array.isArray(notesList) ? (
                    notesList.map((note, index) => (
                        <div key={index} className="note" contentEditable={true}>
                            {note}
                        </div>
                    ))
                ) : (
                    <div className="note" contentEditable={true}>
                        {notesList}
                    </div>
                )}
            </div>
        ));
    };

    const downloadNotes = () => {
        const doc = new jsPDF();
        const title = prompt("Enter a title for your notes:", "Meeting Notes");
        if (!title) return;

        const notesContainer = document.getElementById('notes-container');

        html2canvas(notesContainer, { scale: 2 }).then(canvas => {
            const imgData = canvas.toDataURL('image/png');
            const pdf = new jsPDF('p', 'mm', 'a4');

            const imgWidth = 210;
            const pageHeight = 297;
            const imgHeight = canvas.height * imgWidth / canvas.width;
            let heightLeft = imgHeight;

            let position = 0;

            pdf.addImage(imgData, 'PNG', 0, position, imgWidth, imgHeight);
            heightLeft -= pageHeight;

            while (heightLeft >= 0) {
                position = heightLeft - imgHeight;
                pdf.addPage();
                pdf.addImage(imgData, 'PNG', 0, position, imgWidth, imgHeight);
                heightLeft -= pageHeight;
            }

            pdf.save(`${title}.pdf`);
        });
    };

    return (
        <div>
            <div id="notes-container">
                {renderNotes()}
            </div>
            <div id="bottom-right-container">
                <button className="download-btn" onClick={downloadNotes}>Download Notes as PDF</button>
                <span className="instruction-text">Click any note to edit</span>
            </div>
        </div>
    );
};

export default NotesUI;
