/* COMMENTED OUT MEDIARECORDER-BASED HANDLERS */
import React, { useState } from 'react';
import NotesUI from './NotesUI';
import { startRecording, stopRecording } from './services/audioRecording';
import { sendTranscriptToBackend } from './services/noteService';

function App() {
    const [isRecording, setIsRecording] = useState(false);
    const [generatedNotes, setGeneratedNotes] = useState({}); // Holds the notes

    const handleStartRecording = async () => {
        setIsRecording(true);
        await startRecording();
    };

    const handleStopRecording = async () => {
        await stopRecording();
        const transcript = "Spider-Man and Iron Man are best friends..."; // Sample, get the actual one from your process
        const notes = await sendTranscriptToBackend(transcript); // Send transcript to backend
        setGeneratedNotes(notes); // Store notes in state
        setIsRecording(false);
    };

    return (
        <div className="App">
            <h1>Audio Notes Recorder</h1>
            <button onClick={handleStartRecording} disabled={isRecording}>Start Recording</button>
            <button onClick={handleStopRecording} disabled={!isRecording}>Stop Recording</button>

            {/* Pass notes to NotesUI */}
            <NotesUI notes={generatedNotes} />
        </div>
    );
}

export default App;
