// Initialize WebkitSpeechRecognition
function initializeRecognition() {
    recognition = new window.SpeechRecognition();
    recognition.continuous = true;  // Allow continuous recognition
    recognition.interimResults = true;  // Capture partial results for real-time updates
    recognition.lang = 'en-US';  // Set recognition language

    recognition.onresult = (event) => {
        logWithLimit("Recognition received results:", event);
        let interimTranscript = '';
        for (let i = event.resultIndex; i < event.results.length; i++) {
            const result = event.results[i];
            if (result.isFinal) {
                finalTranscript += result[0].transcript;
                logWithLimit('Final Transcript So Far: ' + finalTranscript);
            } else {
                interimTranscript += result[0].transcript;
            }
        }
        if (interimTranscript.length > 0) {
            logWithLimit('Interim Transcript: ' + interimTranscript);
        }
    };

    recognition.onend = () => {
        logWithLimit('Speech recognition ended.');
        if (isRecognitionRunning) {
            if (restartAttempts < 5) {
                restartAttempts++;
                logWithLimit('Restarting speech recognition...');
                recognition.start();  // Restart recognition automatically
            } else {
                logWithLimit('Max restart attempts reached. Please check for issues.');
            }
        }
    };

    recognition.onerror = (event) => {
        logWithLimit('Speech Recognition Error: ' + event.error);
        if (event.error === 'no-speech') {
            logWithLimit('No speech detected.');
            setTimeout(() => {
                if (!isRecognitionRunning) {
                    startSpeechRecognition();
                }
            }, 1000);
