// Start speech recognition function
export async function startSpeechRecognition() {
    const permissionGranted = await requestMicPermission();  
    if (!permissionGranted) return;

    if (isRecognitionRunning) {
        logWithLimit("Speech recognition is already running.");
        return;
    }

    finalTranscript = '';  
    initializeRecognition();  
    try {
        recognition.start();  
        isRecognitionRunning = true;  
        logWithLimit('Speech recognition started.');
        restartAttempts = 0;  // Reset restart attempts
    } catch (error) {
        logWithLimit('Error starting speech recognition: ' + error);
    }
