// Stop speech recognition function (and send transcript)
export function stopSpeechRecognition() {
    if (!isRecognitionRunning) {
        logWithLimit("Speech recognition is not running.");
        return;
    }
    logWithLimit('Stopping speech recognition...');
    recognition.stop();  
    isRecognitionRunning = false;  

    if (finalTranscript) {
        logWithLimit('Sending final transcript to backend.');
        processTranscript(finalTranscript);  
        finalTranscript = '';  
    }
}

// Process the final transcript and send to backend
export const processTranscript = async (transcript) => {
    try {
        const response = await fetch('http://localhost:3001/generateNotes', {
            method: 'POST',
            headers: {
                'Content-Type': 'application/json',
            },
            body: JSON.stringify({ transcript }),  
        });

        if (!response.ok) {
            throw new Error(`Server error: ${response.status}`);
        }

        const result = await response.json();
        logWithLimit('Generated notes: ' + JSON.stringify(result));  
    } catch (error) {
        logWithLimit('Error sending transcript to backend: ' + error);  
    }
