let mediaRecorder = null; // Declare globally

export async function startRecording() {
    try {
        // Request screen sharing (desktop) video stream
        const screenStream = await navigator.mediaDevices.getDisplayMedia({
            video: true, // Request video only for screen sharing
            audio: false // Desktop audio might not be supported in Safari
        });

        // Request microphone audio separately
        const micStream = await navigator.mediaDevices.getUserMedia({
            audio: true
        });

        // Combine the streams
        const combinedStream = new MediaStream([
            ...screenStream.getAudioTracks(),
            ...micStream.getAudioTracks()
        ]);

        // Initialize MediaRecorder
        mediaRecorder = new MediaRecorder(combinedStream);
        let audioChunks = [];

        mediaRecorder.ondataavailable = (event) => {
            if (event.data.size > 0) {
                audioChunks.push(event.data);
            }
        };

        mediaRecorder.start();

        mediaRecorder.onstop = () => {
            const audioBlob = new Blob(audioChunks, { type: 'audio/webm' });
            processAudioChunk(audioBlob);
        };
    } catch (error) {
        console.error('Error capturing audio:', error);
    }
}

export function stopRecording() {
    if (mediaRecorder && mediaRecorder.state !== 'inactive') {
        mediaRecorder.stop();
    } else {
        console.error('No active recording to stop.');
    }
}

export const processAudioChunk = async (audioBlob) => {
    if (!(audioBlob instanceof Blob)) {
        console.error('audioBlob is not a Blob instance.');
        return;
    }

    const formData = new FormData();
    formData.append('audio', audioBlob, 'audio.webm');

    try {
        const response = await fetch('http://localhost:3001/transcribe', {
            method: 'POST',
            body: formData,
        });

        if (!response.ok) {
            throw new Error(`Server error: ${response.status}`);
        }

        const result = await response.json();
        console.log('Transcription result:', result);
    } catch (error) {
        console.error('Error sending audio to backend:', error);
    }
}
