let recognition, isRecognitionRunning = false;
let finalTranscript = ''; // Accumulate final transcript
let inactivityTimeout; // To track the inactivity timeout
const INACTIVITY_THRESHOLD = 10000; // Increased to 10 seconds
const RESTART_DELAY = 1000;  // 1-second delay before restarting

// Request microphone permission
async function requestMicPermission() {
    try {
        const stream = await navigator.mediaDevices.getUserMedia({ audio: true });
        stream.getTracks().forEach(track => track.stop());
        console.log('Microphone permission granted');
        return true;
    } catch (error) {
        alert('Microphone access is required.');
        console.log('Microphone permission denied');
        return false;
    }
}

// Initialize SpeechRecognition API
function initializeRecognition() {
    window.SpeechRecognition = window.SpeechRecognition || window.webkitSpeechRecognition;
    if (!window.SpeechRecognition) {
        alert('Browser does not support SpeechRecognition API.');
        console.log('SpeechRecognition API not supported');
        return;
    }

    recognition = new window.SpeechRecognition();
    recognition.continuous = true; // Keep recognition continuous
    recognition.interimResults = true;
    recognition.lang = 'en-US';

    recognition.onresult = (event) => {
        clearTimeout(inactivityTimeout); // Clear inactivity timeout on result
        let interim = '';
        for (let i = event.resultIndex; i < event.results.length; i++) {
            const transcript = event.results[i][0].transcript;
            if (event.results[i].isFinal) {
                finalTranscript += transcript + ' ';  // Append to final transcript
                console.log('Final Transcript:', finalTranscript);
            } else {
                interim += transcript;
                console.log('Interim Transcript:', interim);
            }
        }

        // Restart if no activity for the threshold duration
        inactivityTimeout = setTimeout(() => {
            console.log('No activity detected. Restarting recognition...');
            restartRecognition();
        }, INACTIVITY_THRESHOLD);  // Now waits 10 seconds before deciding no activity
    };

    recognition.onerror = (event) => {
        console.log('Recognition error:', event.error);
        restartRecognition();
    };

    recognition.onend = () => {
        console.log('Recognition ended.');
        if (isRecognitionRunning) {
            console.log('Recognition stopped unexpectedly. Restarting...');
            restartRecognition(); // Restart when stopped unexpectedly
        }
    };

    console.log('Speech recognition initialized.');
}

// Start Speech Recognition
export async function startSpeechRecognition() {
    if (!isRecognitionRunning) {
        finalTranscript = '';  // Reset transcript for new session
        const permissionGranted = await requestMicPermission();
        if (!permissionGranted) return;

        if (!recognition) initializeRecognition();
        startRecognition();
    }
}

function startRecognition() {
    if (!isRecognitionRunning) {
        console.log('Starting recognition...');
        try {
            recognition.start();
            isRecognitionRunning = true;
        } catch (error) {
            console.log('Recognition start failed:', error);
        }
    } else {
        console.log('Recognition already running.');
    }
}

// Restart Speech Recognition with delay
function restartRecognition() {
    if (isRecognitionRunning) {
        console.log('Stopping recognition before restarting...');
        recognition.stop();
        isRecognitionRunning = false;

        setTimeout(() => {
            console.log('Restarting recognition...');
            startRecognition();  // Restart after delay
        }, RESTART_DELAY);  // Delay before restart
    }
}

// Stop Speech Recognition
export function stopRecognition() {
    if (isRecognitionRunning) {
        console.log('Stopping recognition...');
        recognition.stop();
        clearTimeout(inactivityTimeout); // Clear timeout when stopping
        isRecognitionRunning = false;
    }
}

// Export the final transcript
export const getFinalTranscript = () => finalTranscript;
